    is.String(b) && (b = nice.Type(b));
    return a === b || b.isPrototypeOf(a);
  }

(...as) => {
  let v;
  for(let i in as){
    v = nice(as[i]);
    if(is.Something(v) && (!v._getResult || v._getResult() !== false))
      return v;
  }
  return v || nice.Nothing();
}

(...as) => {
