(...as) => {
  let count = 0;
  for(let i in as){
    const v = nice(as[i]);
    if(is.Something(v) && (!v._getResult || v._getResult() !== false))
      count++;
  }
  return nice(count && count < as.length ? true : false);
}

function each(o, f){
  for(let k in o._items)
    if(k !== '_nt_')
      if(is.Stop(f(o._items[k], k)))
        break;
  return o;
}

function reduce(o, f, res){
  for(let k in o._items)
    res = f(res, o._items[k], k);
  return res;
}

function mapToArray(c, f){
  return c.reduceTo.Array((a, v, k) => a.push(f(c.get(k), k)));
}

function map(c, f){
  const res = c._type();
  for(let i in c())
    res.set(i, f(c.get(i), i));
  return res;
}

function filter(c, f){
  return c._type().apply(z => c.each((v, k) => f(v,k) && z.set(k, v)));
}

function sum(c, f){
  return c.reduceTo.Num((sum, v) => sum.inc(f ? f(v) : v()));
}

function some(c, f){
  for(let i in c._items)
    if(f(c._items[i], i))
      return true;
  return false;
}

function every(c, f){
  for(let i in c._items)
    if(!f(c._items[i], i))
      return false;
  return true;
}

function find(c, f){
  for(let i in c._items)
    if(f(c._items[i], i))
      return items[i];
  return nice.NotFound();
}

function findKey(c, f){
  for(let i in c._items)
    if(f(c._items[i], i))
      return i;
  return nice.NotFound();
}

function count(o, f) {
  let n = 0;
  o.each((v, k) => f(v, k) && n++);
  return nice.Num(n);
}

function getProperties(z){
  const res = [];
  for(let i in z) z[i]._isProperty && res.push(z[i]);
  return res;
}

function listen(source, f) {
  const ss = source._subscribers;
  if(!ss.includes(f)){
    ss.push(f);
    isResolved(source) ? f(source._result) : source.compute();
  }
  return source;
}

function listenOnce(source, f) {
  isResolved(source) || source.compute();
  if(isResolved(source))
    return f(source._result);
  const _f = v => {
    f(v);
    source.unsubscribe(_f);
  };
  source._subscribers.push(_f);
  return source;
}

function unsubscribe(s, target){
  nice._removeArrayValue(s._subscribers, target);
  s._subscribers.length || s._subscriptions.forEach(_s => _s.unsubscribe(s));
}

function bind(y, x) {
  y(x());
  x.listen(y);
  y.listen(x);
  return y;
}

function unbind(y, x) {
  nice.unsubscribe(y, x);
  nice.unsubscribe(x, y);
  return y;
}

(a, f, res) => {
  each(a, (v, k) => res = f(res, v, k));
  return res;
}

(a, f, res) => {
  a.eachRight((v, k) => res = f(res, v, k));
  return res;
}

(z, ...a) => {
