        return c.each(_c => z.add(_c));
      if(c === undefined || c === null)
        return;
      if(typeof c === 'string' || nice.isStr(c))
        return z.children(c);
      if(nice.isNumber(c) || nice.isNum(c))
        return z.children(c);
