Action add ( Html z, ...children ).source:
function add(z, ...children) {
    children.forEach(c => {
      if(nice.isArray(c))
        return _each(c, _c => z.add(_c));
      if(nice.isArr(c))
        return c.each(_c => z.add(_c));
      if(c === undefined || c === null)
        return;
      if(typeof c === 'string' || nice.isStr(c))
        return z.children(c);
      if(nice.isNumber(c) || nice.isNum(c))
        return z.children(c);
      if(c === z)
        return z.children(`Errro: Can't add element to itself.`);
      if(!c || !nice.isAnything(c))
        return z.children('Bad child: ' + c);
      c.up = z;
      c._up_ = z;
      z.children.push(c);
    });
  }

Action pull ( Arr z, item ).source:
(z, item) => {
  const k = is.Value(item)
    ? z.items.indexOf(item)
    : z.findKey(v => item === v());
  (k === -1 || k === undefined) || z.removeAt(k);
}

Action insertAt ( Arr z, Number i, v ).source:
(z, i, v) => {
  i = +i;
  const old = z._items;
  z._oldValue = z._oldValue || {};
  z._newValue = z._newValue || {};
  z._newValue[i] = v;
  z._items = [];
  _each(old, (_v, k) => {
    +k === i && z._items.push(v);
    z._items.push(_v);
  });
  if(old.length <= i)
    return z._items[i] = v;
}

Action insertAfter ( Arr z, target, v ).source:
(z, target, v) => {
  let i;
  for(i in z._items)
    if(is(target, z._items[i]))
      break;
  return z.insertAt(+i+1, v);
}

Action removeAt ( Arr z, i ).source:
(z, i) => {
  i = +i;
  const old = z._items;
  z._oldValue = z._oldValue || {};
  z._oldValue[i] = old[i];
  z._items = [];
  _each(old, (v, k) => +k === i || z._items.push(v));
}

Func forEach ( Arr z, Function f ).source:
function each(z, f){
  const a = z._items;
  const l = a.length;
  for (let i = 0; i < l; i++)
    if(nice.isStop(f(z._items[i], i)))
      break;
  return z;
}

Func eachRight ( Array a, Function f ).source:
function eachRight(a, f){
  let i = a.length;
  while (i-- > 0)
    if(nice.isStop(f(a[i], i)))
      break;
  return a;
}

Action fill ( Arr z, v, start = 0, end ).source:
function fill(z, v, start = 0, end){
  const l = z._items.length;
  end === undefined && (end = l);
  start < 0 && (start += l);
  end < 0 && (end += l);
  for(let i = start; i < end; i++){
    z.set(i, v);
  }
}

Mapping sortBy ( Arr a, f ).source:
function sortBy(a, f){
  f = nice.mapper(f);
  const res = Arr();
  const source = a._items;
  source
    .map((v, k) => [k, f(v)])
    .sort((a, b) => +(a[1] > b[1]) || +(a[1] === b[1]) - 1)
    .forEach(v => res.push(source[v[0]]));
  return res;
}

Mapping sortedIndex ( Arr a, v, f = (a,.source:
(a, v, f = (a, b) => a - b) => {
