(...as) => {
  let v;
  for(let i in as){
    v = nice(as[i]);
    if(is.Something(v) && (!v.getResult || v.getResult() !== false))
      return nice(false);
  }
  return nice(true);
}

(...as) => {
  let count = 0;
  for(let i in as){
    const v = nice(as[i]);
    if(is.Something(v) && (!v.getResult || v.getResult() !== false))
      count++;
  }
  return nice(count && count < as.length ? true : false);
}

function has(z, i) {
  if(i.pop){
    if(i.length === 1){
      i = i[0];
    } else {
      const head = i.shift();
      return z.has(head)() ? z.get(head).has(i) : false;
    }
  }
  return z.getResult().hasOwnProperty(i) ? true : false;
}

function get(z, i) {
  if(i.pop){
    if(i.length === 1){
      i = i[0];
    } else {
      return z.get(i.shift()).get(i);
    }
  }
  const vs = z.getResult();
  if(is.Str(i))
    i = i();
  if(!vs.hasOwnProperty(i)){
    const types = z._type.types;
    if(i in vs === false){
      if(types && types[i])
        vs[i] = types[i].defaultValue();
      else
        return nice.NOT_FOUND;
    } else {
      if(typeof vs[i] === 'object')
        vs[i] = create(vs[i], (types && types[i] && types[i].defaultValue()) || {});
    }
  }
  const res = nice.toItem(vs[i]);
  res._parent = z;
  res._parentKey = i;
  res.setResult = setResult.bind(res);
  res.getResult = getResult.bind(res);
  return res;
}

(z, path, v) => {
  let data = z.getResult();
  let k = path;
  if(path.pop){
    while(path.length > 1){
      k = nice.unwrap(path.shift());
      if(!data.hasOwnProperty(k)){
        data[k] = {};
        data = data[k];
      } else if(data[k]._nt_){
        if(typeof data[k] !== 'object')
          throw `Can't set property ${k} of ${data[k]}`;
        else
          data = data[k];
      } else if(typeof data[k] !== 'object') {
        throw `Can't set property ${k} of ${data[k]}`;
      } else {
        data = data[k];
      }
    }
    k = path[0];
  }
  k = nice.unwrap(k);
  const type = z._itemsType;
  data[k] = type
    ? (v._type && v._type === type ? v : type(v)).getResult()
    : Switch(v)
      .Box.use(v => v)
      .primitive.use(v => v)
      .nice.use(v => v.getResult())
      .Object.use(v => v)
      .function.use(v => v)
      ();
  return z;
}

function each(o, f){
  for(let k in o.getResult())
    if(k !== '_nt_')
      if(f(o.get(k), k) === nice.STOP)
        break;
  return o;
}

function reduce(o, f, res){
  for(let k in o.getResult())
    res = f(res, o.get(k), k);
  return res;
}

function mapToArray(c, f){
  return c.reduceTo.Array((a, v, k) => a.push(f(c.get(k), k)));
}

function map(c, f){
  const res = c._type();
  for(let i in c())
    res.set(i, f(c.get(i), i));
  return res;
}

function filter(c, f){
  return c._type().apply(z => c.each((v, k) => f(v,k) && z.set(k, v)));
}

function sum(c, f){
  return c.reduceTo.Num((sum, v) => sum.inc(f ? f(v) : v));
}

function some(c, f){
  const items = c.getResult();
  for(let i in items)
    if(f(items[i], i))
      return true;
  return false;
}

function every(c, f){
  const items = c.getResult();
  for(let i in items)
    if(!f(items[i], i))
      return false;
  return true;
}

function find(c, f){
  const items = c.getResult();
  for(let i in items)
    if(f(items[i], i))
      return items[i];
  return nice.NOT_FOUND;
}

function findKey(c, f){
  const items = c.getResult();
  for(let i in items)
    if(f(items[i], i))
      return i;
  return nice.NOT_FOUND;
}

function count(o, f) {
  let n = 0;
  o.each((v, k) => f(v, k) && n++);
  return nice.Num(n);
}

function getProperties(z){
  const res = [];
  for(let i in z) z[i]._isProperty && res.push(z[i]);
  return res;
}

function listen(source, f) {
  const ss = source._subscribers;
  if(!ss.includes(f)){
    ss.push(f);
    source.isResolved() ? f(source.state) : source.compute();
  }
  return source;
}

function listenOnce(source, f) {
  source.isResolved() || source.compute();
  if(source.isResolved())
    return f(source.state);
  const _f = v => {
    f(v);
    source.unsubscribe(_f);
  };
  source._subscribers.push(_f);
  return source;
}

function unsubscribe(s, target){
  nice._removeArrayValue(s._subscribers, target);
  s._subscribers.length || s._subscriptions.forEach(_s => _s.unsubscribe(s));
}

function bind(y, x) {
  y(x());
  x.listen(y);
  y.listen(x);
  return y;
}

function unbind(y, x) {
  nice.unsubscribe(y, x);
  nice.unsubscribe(x, y);
  return y;
}

(a, f, res) => {
