function eachRight(z, f){
  const a = z.getResult();
  let i = a.length;
  while (i-- > 0)
    if(f(z.get(i), i) === nice.STOP)
      break;
  return z;
}

function fill(z, v, start = 0, end){
  const l = z.getResult().length;
  end === undefined && (end = l);
  start < 0 && (start += l);
  end < 0 && (end += l);
  for(let i = start; i < end; i++){
    z.set(i, v);
  }
}

function map(a, f){
  return a.reduceTo.Arr((z, v, k) => z.push(f(v, k)));
}

function filter(a, f){
  return a.reduceTo(Arr(), (res, v, k) => f(v, k, a) && res.push(v));
}

function sortBy(a, f){
  f = nice.mapper(f);
  const res = Arr();
  const source = a.getResult();
  source
    .map((v, k) => [k, f(v)])
    .sort((a, b) => +(a[1] > b[1]) || +(a[1] === b[1]) - 1)
    .forEach(v => res.push(source[v[0]]));
  return res;
}

(a, v, f = (a, b) => a - b) => {
  let i = a.size;
  a.each((vv, k) => {
    if(f(v, vv) <= 0){
      i = k;
      return nice.STOP;
    }
  });
  return i;
}

function intersperse(a, separator) {
  const res = Arr();
  const last = a.size - 1;
  a.each((v, k) => res.push(v) && (k < last && res.push(separator)));
  return res;
}

