function each(z, f){
    let i = z.start();
    let end = z.end();
    let n = 0;
    while(i <= end) f(i++, n++);
  }

function map(f){
    let i = this.start();
    let n = 0;
    const a = nice.Arr();
    while(i <= this.end()) a(f(i++, n++));
    return a;
  }

function toArray(z){
    const a = [];
    const end = z.end();
    let i = z.start();
    while(i <= end) a.push(i++);
    return a;
  }

function includes(z, n){
    return n >= z.start && n <= z.end;
  }

function within(v, r){
  return v >= r.start && v <= r.end;
}

function on(z, name, f){
    if(name === 'domNode' && nice.isEnvBrowser){
      if(!z.id())
        throw `Give element an id to use domNode event.`;
      const el = document.getElementById(z.id());
      el && f(el);
    }
    nice.Switch(z.eventHandlers(name))
      .Nothing.use(() => z.eventHandlers(name, [f]))
      .default.use(a => a.push(f));
  }

(z, ...vs) => {
    const current = z.attributes('className').or('')();
    if(!vs.length)
      return current;
    const a = current ? current.split(' ') : [];
    vs.forEach(v => !v || a.includes(v) || a.push(v));
    z.attributes('className', a.join(' '));
    return z;
  }

function scrollTo(z, offset = 10){
    z.on('domNode', node => {
      node && window.scrollTo(node.offsetLeft - offset, node.offsetTop - offset);
    });
    return z;
  }

function add(z, ...children) {
    children.forEach(c => {
      if(is.Array(c))
        return _each(c, _c => z.add(_c));
      if(is.Arr(c))
        return c.each(_c => z.add(_c));
      if(c === undefined || c === null)
        return;
      if(is.String(c))
        return z.children(c);
      if(is.Number(c))
        return z.children('' + c);
      if(c === z)
        return z.children(`Errro: Can't add element to itself.`);
      if(!c || !is.Anything(c))
        return z.children('Bad child: ' + c);
      c.up = z;
      c._up_ = z;
      z.children.push(c);
    });
  }
